import React, { useState, useEffect } from "react";
import axios from "axios";

function AdminInventory() {
  const [medicines, setMedicines] = useState([]);
  const [newMedicine, setNewMedicine] = useState({ name: "", stock: 0 });

  const fetchMedicines = () => {
    axios.get("http://localhost:5000/api/medicines/all")
      .then(res => setMedicines(res.data))
      .catch(err => console.error(err));
  };

  useEffect(() => {
    fetchMedicines();
  }, []);

  const handleAdd = () => {
    axios.post("http://localhost:5000/api/medicines/add", newMedicine)
      .then(() => {
        fetchMedicines();
        setNewMedicine({ name: "", stock: 0 });
      })
      .catch(err => console.error(err));
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:5000/api/medicines/delete/${id}`)
      .then(() => fetchMedicines())
      .catch(err => console.error(err));
  };

  return (
    <div className="container">
      <h2>Pharmacy Inventory (Admin)</h2>

      <h4>Add New Medicine</h4>
      <input
        type="text"
        placeholder="Medicine Name"
        value={newMedicine.name}
        onChange={(e) => setNewMedicine({ ...newMedicine, name: e.target.value })}
      />
      <input
        type="number"
        placeholder="Stock"
        value={newMedicine.stock}
        onChange={(e) => setNewMedicine({ ...newMedicine, stock: e.target.value })}
      />
      <button onClick={handleAdd}>Add Medicine</button>

      <h4>Current Stock</h4>
      <ul>
        {medicines.map((med) => (
          <li key={med.id}>
            {med.name} ({med.stock}){" "}
            <button onClick={() => handleDelete(med.id)}>‚ùå Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AdminInventory;
